import { useEffect, useState } from 'react';

function FormularioAddPersona() {

    function peticionGuardar() {
        const [error, setError] = useState(null);
        const [isLoaded, setIsLoaded] = useState(false);
        useEffect(() => {
            fetch("http://localhost:8080/persona/all")
                .then(res => res.json())
                .then(
                    (result) => {
                        setIsLoaded(true);
                        setPersonas(result);
                    },

                    (error) => {
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }, [])
    }

    return (
        <form action='http://localhost:8080/persona/add' method='post'>
            <label for="nombre">Nombre
                <input type="text" name="nombre" placeholder="Nombre" id="nombre" />
            </label>
            <label for="edad">Edad
                <input type="number" name="edad" placeholder="Edad" id="edad" />
            </label>
            <input type="submit" >Guardar</input>
        </form>
    );
}
export default FormularioAddPersona;
