function FormularioAddPersona () {
    
    function peticionGuardar(){
        useEffect(() => {
            fetch("http://localhost:8080/persona/all")
              .then(res => res.json())
              .then(
                (result) => { 
                  setIsLoaded(true);
                    setPersonas(result);
                },
                
                (error) => {
                  setIsLoaded(true);
                  setError(error);
                }
              )
          }, [])
          if (error) {
            return <div>Error: {error.message}</div>;
          } else if (!isLoaded) {
            return <div>Loading...</div>;
          } else {
            return (
          
              <div className="App">
                <header className="App-header">
                  <FormularioAddPersona/>
                  <ListaPersonas personas={personas}/>   
                </header>
              </div>
            );
          }
    }

    return (
        <>
            <label for="nombre">Nombre
                <input type="text" name="nombre" placeholder="Nombre" id="nombre"/>
            </label>
            <label for="edad">Edad
                <input type="number" name="edad" placeholder="Edad" id="edad"/>
            </label>
            <button type="button" onClick={peticionGuardar}>Guardar</button>
        </>
    );
}
export default FormularioAddPersona;
